package com.ureca.day1;

public class LoopTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		while(i >= 0) {
			System.out.println("i=" + i + " ");
			i++;
		}
		System.out.println();
		
		
		for (int j = 1; j <= 5; j++) {
			System.out.println("j=" + j + " ");
		}
		
		
		
	}

}
